package assignment3;


/*
 what will be size, type and default value of these variables?
		int,
		double,
		float,
		long,
		short,
		byte,
		boolean,
		char
 */
public class Datatype {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
				int a=100000; // The int data type is 32-bit
				double d=12.3; // double data type is 64-bit
				float f=234.5f;// float data type is 32-bit
				long a1=100000l;//long data type is 64-bit
				short s=10000; // short data type is 16-bit
				byte z=10; // byte data type is 8-bit
				boolean one = false; // boolean data type is 1-bit
				char h='A'; // char data type is 16-bit
				
				System.out.println(a);
				System.out.println(d);
				System.out.println(f);
				System.out.println(a1);
				System.out.println(s);
				System.out.println(z);
				System.out.println(one);
				System.out.println(h);
	
	
	}

}
