package org.la.student.jamal.loob;

public class ForLoob11 {

	//11.	wap to print 1 time a.
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i=0; i<1; i++) {
			System.out.println("a");
		}
		int j=0;
		while (j==0) {
			System.out.println("this is while : a");
			
			j++;
		}
		
	}

}
