class array42UpperToLower {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		

	char a[]= {'A','B','C'};
	char b []=  new char [a.length];	
		
		for(int i=0;i<a.length;i++) {
		b[i]=Character.toLowerCase(a[i]);	
			
		System.out.println(b[i]);
		}
		

		
	}
	}
