package org.la.student.nizaam.increment.decrement;

public class PostDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//wap to do post Decrement a number and assign its value to other number and print both number
		int a=3,b;
		a--;
		b=a;
		System.out.println("Post Increment of a assigned to b : "+b+ " value of a after postIncrement "+a);

	}

}
