package org.la.student.nizaam.loop.whilee;

public class Q50OddNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//	50.	Wap to print all odd number between 1 to 100.
		
		int i=1;
		
		while(i<=100) {
			if(i%2!=0) {
				System.out.println(i);
			}
			i++;
		}
	}

}
