public class Q12SmallestofThreeNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//	12.	wap to find smallest number among 3 numbers
		
		int a=45,b=30,c=20;
		int smallest = (a<b)? (a<c)?a:c:b;
		System.out.println("smallest of given numbers is "+smallest);
	}

}
