public class Q37NumberInRangeorNot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//	37. wap to check whether number is between -100 to 50
		int num=-5;
		if(num>=-100 && num <=50)
			System.out.println("Given number is in range -100 to 50");
		else
			System.out.println("Given number is not in range -100 to 50");

	}

}
