public class Q49CapitalorNot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//	49. wap to check whether a given character is capital 'A' or not 
		
		char c='F';
		if(c>='A' && c<='Z')
			System.out.println("Given character is Capital character");
		else
			System.out.println("Given character is not Capital Character");
	}

}
