public class Q5DivisibleorNot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//	5.	wap to find whether a number is divisible by a given number completely.
		int dividend =25;
		int divisor = 5;
		
		if(dividend%divisor==0) {
			System.out.println("Given number is completely Divisible by divisor");
		}
		else
			System.out.println("Given number is not completely divisible by divisor");
		

	}

}
