package org.la.student.tania;

public class Boolean_if_else_Exp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
        // boolean operation with if-else
		
		boolean a= true;
		boolean b= false;
		if(a && b) {
			System.out.print("a and b both are false");
		}
		else if(a && b) {
			System.out.print("either a or b is false ");
		}
			
	}

}
