package org.la.student.tania;

public class Control_Statement_41 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//46. wap to find greatest number among three numbers
		int a=3;
		int b=6;
		int c=9;
		if(a>b && a<b) {
			System.out.println(a+ " greatest");
		}
		else if(b>a && b>c ){
			System.out.println(b+ " greatest");
		}
		else {
			System.out.println(c+ " greatest");
		}
	}
	}


