package org.la.student.tania;

public class Control_Statement_43 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//48. wap to find least number among three numbers
		int a=3;
		int b=6;
		int c=9;
		if(a<b && a<b) {
			System.out.println(a+ " Least");
		}
		else if(b<a && b<c ){
			System.out.println(b+ "Least");
		}
		else {
			System.out.println(c+ " Least");
		}
	}

}
