package org.la.student.tania;

public class Control_statement_10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
      // wap to find a smallest number among three number
		
		
		int a=1,b=2,c=3;
		if(a<b && a<c) {
			System.out.println("smallest number= "+a);
		}
		else if(b<a && b<c) {
			System.out.println("smallest number= " +b);
		}
		else {
			System.out.println("smallest number= "+c);
		}
	}

}
