package org.la.student.tania;

public class Control_statement_13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		 // wap to check whether a given character is lower case (a toz)or upper case(A to Z)
		
		char alpha = 'A';

		if(alpha >='A' && alpha <='Z')
		{
		System.out.println("Upper case Alphabet");
		}
		else if(alpha >= 'a' && alpha <= 'z')
		{
		System.out.println("Lower case Alphabet");
		}

	}

}
