package org.la.student.tania;

public class Control_statement_14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
           
		// wap to check whether a given character is lower case (a toz)or upper case(A to Z)
				 // Hint: can use ASCII code to solve the problem
		
		char alpha = 'a';
		
		if(alpha >=65 && alpha <=90)
		{
		System.out.println("Upper case Alphabet");
		}
		else if(alpha >= 97 && alpha <= 122)
		{
		System.out.println("Lower case Alphabet");
		}
	}

}
