public class Control_statement_5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


		//wap to find a number is multiple of 10 or not
		int y=5;
		int z=y*2;
		if(z==10) {
			System.out.println(z+ " is a multiple of 10");
		} 
		
		else { 
			System.out.println(z+ " is not multiple of 10");
		}
	}

}
