package org.la.student.tania;

public class Logical_AND4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 6, y = 7;
		boolean res = (++x==2) && (--y==1); 
		
		System.out.println("res: "+res);
		System.out.println("x: "+x);
		System.out.println("y: "+y);
	}

}
