package org.la.student.tania;

public class Logical_operation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// logical operation
		int a = 1;
		int b = 2;
		if(a==1 || ++b==3)
		System.out.println("hello world");
		
		int c = 1;
		int d= 2;
		if(c==1 && ++d==3)
		System.out.println("hello world");
		
		int e = 1;
		int f = 2;
		if(e!=1 || f++==3)
		System.out.println("hello World");
		
		
		int x= 1;
		int y = 2;
		if(x!=1 && y++==3)
		System.out.println("hello world");
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		

	}
}

	
