package org.la.student.tania;

public class Logical_operation_8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int a=31, b=40;
        int c=b-a;
        b=c%a;
        b=b/a;
        a=++c;
		b=(a- --b) *c;
		System.out.println(a);
		System.out.println(b);
		System.out.println(c);
		 
	}

}
