public class Post_increment4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 int a=5;
 int b=6;
 int c=4;
 int d= (a++*b++)-c--;
 System.out.println("a: " +a);
 System.out.println("b: " +b);
 System.out.println("c: " +c);
 System.out.println("d: " +d);
 
	}

}
