public class Pre_increment3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 int a=5;
 int b=6;
 int c=++a;
 int d=++b;
 System.out.println("a :" +a);
 System.out.println("b :" +b);
 System.out.println("c :" +c);
 System.out.println("d :" +d);
		 
	}

}
