public class Whileloop_16 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//16. wap to print all odd numbers between 120 to 100.
		int i=120;
		
		  while(i>=100) {
			  
			  if(i%2!=0)
			  
			System.out.println("Odd " +i);
			  
			i--;	
		  }			
		
	}

}
