package org.la.student.tania;

public class Whileloop_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//3. wap to print first five odd numbers.
		int i=1;
			
		while(i<=10) {
			if(i%2!=0) 
			System.out.println("odd " +i);
			i++;
			
			
		}
				
	}

}
