package org.la.student.tania;

public class Whileloop_4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//4. wap to print first five even numbers.
		int i=1;
		
		while(i<=10) {
			if(i%2==0) 
			System.out.println("even " +i);
			i++;	
		
		
		
		}
	}

}
