public class Logical_op4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
             
		int x = 1, y = 2;
		boolean res = (++x==2) && (--y==1); 
		
		System.out.println("res: "+res);
		System.out.println("x: "+x);
		System.out.println("y: "+y);
	}

}
