public class Loop_Q1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//1.	wap to print number from 1 to 10
		
		int a=1;
		while(a<=10) {
			
			System.out.println(" a= " +a);
			a++;
		}
		
//or
		for(int i=1 ; i<=10; i++) {
			System.out.println(i);
		}
		
		
	}

}
