public class Loop_Q9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  // wap to print numbers which is multiple of 3 between 1 to 22.
     int i=1;
     while(i<=22) {
    	  
    	 if(i%3==0) {
    		 System.out.println(i);
    	 }
    	i++; 
    	
     }
	}

}
