public class Question_43 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//43. wap to print ternary if number is 3 digit
  int a=555;
  if((a>99 && a<999)||(a<-99 && a>-999)){
	  System.out.println("The number is in three digit");
  }
  else {
	  System.out.println("The number is not three digit");
  }
	}

}
