package org.la.student.tania_task1_practice_if_else;

public class Question_48 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//48. wap to find least number among three numbers
  
		int a=2,b=1,c=22;
		if(a<b && a<c  ) { 
			System.out.println("The least number is a: " +a);
		}
		
		else if(b<a && b<c) { 
			System.out.println("the least number is b: " +b);
		
	}
		else {
			System.out.println("the least number is c: " +c);
		}

	}

}
